import React from 'react';
// import logo from './logo.svg';
import './App.css';
import {Component} from 'react';
import {cardList} from './card-list/card-list.component';

class App extends Component{
    constructor(){
        super();
        this.state = {
            // String: 'Hello Umma'
            // monsters: [ //hard coding data inside react
            //     {
            //         name: 'Umma'
            //             id: 'ert'
            //     },
            //     {
            //         name: 'Dracula'
            //     },
            //     {
            //         name: 'John'
            //     },
            //     {
            //         name: 'Zombie'
            //     }
            // ]
            monsters: []
        }//https://jsonplaceholder.typicode.com/users
    }

    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users') //fetch gives a promise that returns a respond. Fetch() is use to consume an API.
        .then(Response => Response.json())
        .then(users => this.setState({monsters: users}))
    }
    render(){
        return ( 
            <div className="App">
                {/* <h1>{this.state.String}</h1>
                <button onClick={ () => this.setState({String: 'How you doing'})}>Click Me</button> */}
                <CardList>{this.state.monsters.map(monster => (<h2 key={monster.id}>{monster.name}</h2>))}</CardList>
            </div>
        );
    }
}

export default App;
